mapper.select:
select u.user_id,u.user_name,u.user_pw from tb_user u


        AND  u.user_id = #{user_id}


        AND  u.user_name = #{user_name}


        AND  u.user_pw = #{user_pw}

select u.user_id,u.user_name,ui.img_path,ui.img_name from tb_user u
left join tb_user_img ui on u.user_id = ui.user_id and ui.img_type = '10001'


        AND  u.user_id = #{user_id}


        AND  u.user_name = #{user_name}


        AND  u.user_pw = #{user_pw}

SELECT EXISTS (
    SELECT 1
    FROM tb_user_signin
    WHERE user_id = #{userId}
      AND signin_date = CURDATE()
) AS is_signed_today;

WITH RECURSIVE consecutive_days AS ( -- 找出最近的一次签到 SELECT signin_date, 1 AS streak_length FROM tb_user_signin WHERE user_id = #{userId} AND signin_date = ( SELECT MAX(signin_date) FROM tb_user_signin WHERE user_id = #{userId} )
UNION ALL
SELECT t.signin_date, cd.streak_length + 1 FROM tb_user_signin t INNER JOIN consecutive_days cd ON t.user_id = #{userId} AND t.signin_date = SUBDATE(cd.signin_date, 1) ) SELECT IFNULL(MAX(streak_length), 0) AS current_consecutive_days FROM consecutive_days;

SELECT signin_date AS signin_date_str
FROM tb_user_signin
WHERE user_id = #{userId}
  AND YEAR(signin_date) = #{year}
  AND MONTH(signin_date) <= #{maxMonth}
  AND MONTH(signin_date) >= #{minMonth}
ORDER BY signin_date ASC

select count(*)
from tb_user_secquestion
where secq_id = #{secq_id} and answer = #{answer}
