mapper.select:
select u.user_id,u.user_name from tb_user u


        AND  u.user_id = #{user_id}


        AND  u.user_name = #{user_name}


        AND  u.user_pw = #{user_pw}

SELECT EXISTS (
    SELECT 1
    FROM tb_user_signin
    WHERE user_id = #{userId}
      AND signin_date = CURDATE()
) AS is_signed_today;
